import axios from "axios";

// const formData = {
//   email: "",
//   password: "",
// };

export const GET_TOKEN = "GET_TOKEN";

export const getToken = (formData) => {
  return (dispatch) => {
    return axios
      .post("http://localhost:3001/api/v1/user/login", formData)
      .then((resToken) => {
        // console.log(resToken.data.body.token);
        localStorage.setItem("token", resToken.data.body.token);
      })
      .then(
        axios
          .post(
            "http://localhost:3001/api/v1/user/profile",
            {},
            {
              headers: {
                Authorization: `Bearer ${localStorage.getItem("token")}`,
              },
            }
          )
          .then((res) => {
            console.log(res);
            dispatch({ type: GET_PROFILE, payload: res.data.body });
          })
      );
  };
};

