import React, { useState } from "react";
import { getProfile } from "../_actions/postAction";
import { connect } from "react-redux";

const Login = ({ getProfile }) => {
  const [foddrmData, setFormData] = useState({
    email: "",
    password: "",
  });

  const { email, password } = formData;

  const onChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const onSubmit = (e) => {
    e.preventDefault();

    // Appeler l'action getprofile avec les données du formulaire
    getProfile({ email, password });
  };
  return (
    <form onSubmit={onSubmit}>
      <div className="form-group">
        <label htmlFor="email">Email</label>
        <input
          type="text"
          name="email"
          placeholder="Email"
          value={email}
          onChange={onChange}
        />
      </div>
      <div className="form-group">
        <label htmlFor="password">Mot de passe</label>
        <input
          type="password"
          name="password"
          placeholder="Mot de passe"
          value={password}
          onChange={onChange}
        />
      </div>
      <button type="submit">Se connecter</button>
    </form>
  );
};

export default connect(null, { getProfile })(Login);
