const initialState = {};

export default function postReducer(state = initialState, action) {
  // serie de coportement a faire (switch)
  switch (action.type) {
    case "GET_PROFILE":
      return {
        ...state,
        user: action.payload,
      };
    default:
      return state;
  }
}
