import axios from "axios";
import { getToken } from "./postTokenAction";

export const GET_USER_PROFILE = "GET_USER_PROFILE";

export const getUserProfile = (formData) => {
  // Accepte formData en tant que paramètre
  return async (dispatch, getState) => {
    try {
      const { token } = await dispatch(getToken(formData));
      console.log(token);

      if (!token) {
        return;
      }
      const response = await axios.post(
        "http://localhost:3001/api/v1/user/profile",
        {},
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      console.log(response);
      dispatch({ type: GET_USER_PROFILE, payload: response.data.body });
    } catch (err) {
      console.log(err);
    }
  };
};
