import axios from "axios";

export const GET_USER_PROFILE = "GET_USER_PROFILE";

export const getUserProfile = (formData) => {
  // Accepte formData en tant que paramètre
  return (dispatch) => {
    // Appelle l'action getToken pour obtenir le token
    getToken(formData) // Utilise les données passées depuis l'appel
      .then((token) => {
        if (token) {
          // Utilise le token dans la requête Axios pour obtenir le profil
          axios
            .post(
              "http://localhost:3001/api/v1/user/profile",
              {},
              {
                headers: {
                  Authorization: `Bearer ${token}`,
                },
              }
            )
            .then((res) => {
              console.log(res.data.body);
              dispatch({ type: GET_USER_PROFILE, payload: res.data.body });
            });
        }
      });
  };
};
